css:
@page { 
    margin: 0px;
}
body {
    margin-left: 30px;
    margin-right: 30px;
    margin-top: 15px;
    margin-bottom: 15px;
    font-family: Arial, sans-serif;
}
.left-strip, .right-strip {
    position: absolute;
    top: 0px;
    height: 770px;
    width: 5cm;
    background: #cecece;
}

.left-strip { left: 10px; }
.right-strip { right: 10px; }
.content {
    position: relative;
}
h1, h2, h3, h4, h5 {
    margin: 0px;
    padding: 0px;
    line-height: 1.1;
    font-weight: normal;
}

.h1-style {
    font-size: 28px;
    font-weight: bold;
}

.h2-style {
    font-size: 22px;
    font-weight: bold;
}

.h3-style {
    font-size: 18px;
}

.h4-style {
    font-size: 16px;
}

.h5-style {
    font-size: 14px;
}
.left-strip-content {
    position: absolute;
    top: 110px;
    left: 10;
    width: 5cm;
    text-align: center;
}
.right-strip-content-fixed {
    position: fixed;
    top: 0;
    right: 0;
    width: 5cm;
    padding: 20px;
}
.right-strip-content {
    position: absolute;
    top: 70px;          
    right: 10px;       
    width: 5cm;
    text-align: center;
}
.right-strip-content img {
    width: 80%;         
    margin-bottom: 10px; 
}
.center-image {
    text-align: center;
    margin-top: 20px;
    margin-left: 5cm; 
    margin-right: 5cm;  
}

.center-image img {
   height: 500px;
   width: 700px;
}

.pdf-table {
    width: 100%;
    border-collapse: collapse;
}

thead {
    display: table-header-group;
}

.header-wrapper {
    justify-content: space-between;
}

.header-left {
    text-align: left;
}

.header-right {
    font-size: 18px;
    font-weight: bold;
    text-align: right;
}

.trims-grid {
    display: flex;
    flex-wrap: wrap;
    gap: 10px;
}

.trim-box {
    width: calc(25% - 10px);
    text-align: center;
}

.trim-box img {
    width: auto;
    height: auto;
    max-height: 150px;
}


.trim-box-trims {
    width: calc(25% - 10px);
    text-align: center;
}

.trim-box-trims img {
    width: auto;
    height: auto;
    max-height: 150px;
}

.colour-center { width: 100%; display: flex; flex-direction: column; align-items: center; }
.colour-row { display: flex; align-items: center; }
.colour-box-list { display: flex; gap: 10px; }
.colour-item { display: flex; flex-direction: column; align-items: center; }
.colour-box { width: 50px; height: 50px; border-radius: 8px; border: 1px solid #000; }
.colour-text { font-size: 12px; width: 50px; text-align: center; }

.trims-section-colour { display: flex; flex-wrap: wrap; gap: 5px; }
.trim-box-colour { width: calc(25% - 10px); text-align: center; page-break-inside: avoid; }
.trim-box-colour img { width: auto; height: auto; border-radius: 5px; max-height: 150px;}

.colour-small-box {
    width: 30px;
    height: 30px;
    border: 1px solid #000;
    border-radius: 2px;
    margin-bottom: 2px;
}

.trim-box-colour {
    padding: 0px;
    width: calc(25% - 10px);
    text-align: center;
}

.colour-box-grid {
    padding-top: 10px;
    display: grid;
    grid-template-columns: repeat(2, max-content);
    grid-auto-rows: max-content;
    gap: 4px 6px; 
    justify-content: center; 
    align-items: start;
}

.colour-item-small { 
    display: flex; 
    flex-direction: column; 
    align-items: center; 
}
.colour-label { 
    font-weight: bold; 
    width: 80px; 
    text-align: center; 
}
.colour-label-small { 
    font-weight: bold; 
    text-align: center; 
}
.line-row {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-right: 30px;
}
.line-row .line {
    flex: 1;        
    height: 1px;
    background: #000; 
    margin-top: 4px; 
}
.footer-wrapper {
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%); 
    width: 95%;
    z-index: 9999;
}
.footer-table {
    width: 100%;
    border-top: 2px solid #000;
    border-collapse: collapse;
    height: 1px;
}

.footer-table td {
    padding: 0;
    margin: 0;
}
.measure-wrap {
   display: flex;
    flex-direction: row;
    justify-content: space-between;
    flex-wrap: wrap;
    height: 500px;
}
.measure-table {
    flex: 1;
    width: 100%;
    padding: 10px;
}

.measure-image img {
    object-fit: contain;
    heighT: 100%;
    width: 100%;
}

.measure-image {
    height: inherit;
}

.measure-table tr:nth-child(even) td {
    background: lightblue;
}

.measure-table tr:nth-child(odd) td {
    background: #ffffff;
}
.measure-table tr th{
    background: orange;
}
.measure-table tr td,
.measure-table tr th {
    page-break-inside: avoid;
}
.image-wrap {
    height: 500px;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;  
    page-break-inside: avoid;
    break-inside: avoid;
}
.image-wrap img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
}
