css:
.table-head, .table-data {
    text-align:center !important;
    border: 1px solid #000;
}
.print-format td{
    font-size: 12px !important;
    padding: 4px !important;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
	{% set items = get_dict_object(doc.cut_panel_movement_json) %}
	<div style="display: flex;width: 100%;font-size:15px;font-weight:600;">
		<div style="width: 50%;text-align:left;">
			Cutting Plan: {{doc.cutting_plan}}
		</div>
		<div style="width:50%;padding-right:10px;text-align:right;">
			Date: {{doc.movement_date}}
		</div>
	</div>
	<div style="display: flex;width: 100%;font-size:15px;font-weight:600;">
		<div style="width: 50%;text-align:left;">
			Lot: {{doc.lot}}
		</div>
		<div style="width:50%;padding-right:10px;text-align:right;">
			Item: {{doc.item}}
		</div>
	</div>
	{% if items.is_set_item %}
		<div>
			{% for colour in items.data %}
				{% if items.data[colour] and items.data[colour]['data'] | length > 0 %}
					<table style="margin-top:10px;">
						{% set panel_length = items.panels[items.data[colour]['part']] | length %}
						<tr>
							<td class='table-head' colspan="2">
								<strong>Lot No:</strong> {{ doc.lot }}
							</td>
							<td class='table-head' colspan="3"><strong>Style Name:</strong> {{ doc.item }}</td>
							<td class='table-head' colspan="{{panel_length + 1}}"><strong>Colour:</strong> {{ colour }}</td>
						</tr>
						<tr>
							<td class='table-data' style="width:10%;"><strong>S.No</strong></td>
							<td class='table-data' style="width:10%;"><strong>Size</strong></td>
							<td class='table-data' style="width:10%;"><strong>Shade</strong></td>
							<td class='table-data' style="width:10%;"><strong>Lay No</strong></td>
							<td class='table-data' style="width:10%;"><strong>Bundle No</strong></td>
							{% for panel in items.panels[items.data[colour]['part']] %}
								<td class='table-head'><strong> {{ panel }}</strong> </td>
							{% endfor %}
							{% if items.panels[items.data[colour]['part']] | length > 0 %}
								<td class='table-data' style="width:10%;"><strong>Total</strong></td>
							{% endif %}
						</tr>
						{% for row in items.data[colour]['data'] %}
							<tr>
								<td class='table-data' style="width:10%;">{{ loop.index }}</td>
								<td class='table-data' style="width:10%;">{{ row.size }}</td>
								<td class='table-data' style="width:10%;">{{ row.shade }}</td>
								<td class='table-data' style="width:10%;">{{ row.lay_no }}</td>
								<td class='table-data' style="width:10%;">{{ row.bundle_no }}</td>
								{% for panel in items.panels[items.data[colour]['part']] %}
									{% if panel in row %}
										{% if row[panel] > 0 and row[panel+"_moved"] %}
											<td class='table-data'>{{ row[panel] }}</td>
										{% else %}
											<td class='table-data'>0</td>
										{% endif %}
									{% else %}
										<td class='table-data'>0</td>
									{% endif %}    
								{% endfor %}
								{% if items.panels[items.data[colour]['part']] | length > 0 %}
									<td class='table-data' style="width:10%;">{{ row.total }}</td>
								{% endif %}
							</tr>
						{% endfor %}
						<tr>
							<td class='table-data' style="width:10%;"><strong>Total</strong></td>
							<td class='table-data' style="width:10%;"></td>
							<td class='table-data' style="width:10%;"></td>
							<td class='table-data' style="width:10%;"></td>
							<td class='table-data' style="width:10%;"></td>
							{% for panel in items.panels[items.data[colour]['part']] %}
								{% if items.total_pieces[colour][panel] > 0 %}
									<td class='table-data'><strong>{{ items.total_pieces[colour][panel] }}</strong></td>
								{% else %}
									<td class='table-data'><strong>0</strong></td>
								{% endif %}
							{% endfor %}
							{% if items.panels[items.data[colour]['part']] | length > 0 %}
								<td class='table-data' style="width:10%;"><strong>{{items.total_bundles[colour]}}</strong></td>
							{% endif %}
						</tr>
					</table>
				{% endif %}   
			{% endfor %}
		</div>
	{% elif items.data %}   
		{% for colour in items.data %}
			{% if items.data[colour] and items.data[colour]['data'] | length > 0 %}
				<table style="margin-top:10px;">
					{% set panel_length = items.panels | length %}
					<tr>
						<td class='table-head' colspan="2">
							<strong>Lot No:</strong> {{ doc.lot }}
						</td>
						<td class='table-head' colspan="3"><strong>Style Name:</strong> {{ doc.item }}</td>
						<td class='table-head' colspan="{{panel_length + 1}}"><strong>Colour:</strong> {{ colour }}</td>
					</tr>
					<tr>
						<td class='table-data' style="width:10%;"><strong>S.No</strong></td>
						<td class='table-data' style="width:10%;"><strong>Size</strong></td>
						<td class='table-data' style="width:10%;"><strong>Shade</strong></td>
						<td class='table-data' style="width:10%;"><strong>Lay No</strong></td>
						<td class='table-data' style="width:10%;"><strong>Bundle No</strong></td>
						{% for panel in items.panels %}
							<td class='table-data'><strong> {{ panel }}</strong> </td>
						{% endfor %}
						{% if items.panels | length > 0 %}
							<td class='table-data' style="width:10%;"><strong>Total</strong></td>
						{% endif %}
					</tr>
					{% for row in items.data[colour]['data'] %}
						<tr>
							<td class='table-data' style="width:10%;">{{ loop.index }}</td>
							<td class='table-data' style="width:10%;">{{ row.size }}</td>
							<td class='table-data' style="width:10%;">{{ row.shade }}</td>
							<td class='table-data' style="width:10%;">{{ row.lay_no }}</td>
							<td class='table-data' style="width:10%;">{{ row.bundle_no }}</td>
							{% for panel in items.panels %}
								{% if panel in row %}
									{% if row[panel] > 0 and row[panel+"_moved"] %}
										<td class='table-data'>{{ row[panel] }}</td>
									{% else %}
										<td class='table-data'>0</td>
									{% endif %}
								{% else %}
									<td class='table-data'>0</td>        
								{% endif %}
							{% endfor %}
							{% if items.panels | length > 0 %}							
								<td class='table-data' style="width:10%;">{{ row.total }}</td>
							{% endif %}
						</tr>
					{% endfor %}
					<tr>
						<td class='table-data' style="width:10%;"><strong>Total</strong></td>
						<td class='table-data' style="width:10%;"></td>
						<td class='table-data' style="width:10%;"></td>
						<td class='table-data' style="width:10%;"></td>
						<td class='table-data' style="width:10%;"></td>
						{% for panel in items.panels %}
							{% if items.total_pieces[colour][panel] > 0 %}
								<td class='table-data'><strong>{{ items.total_pieces[colour][panel] }}</strong></td>
							{% else %}
								<td class='table-data'><strong>0</strong></td>
							{% endif %}
						{% endfor %}
						{% if items.panels | length > 0 %}	
							<td class='table-data' style="width:10%;"><strong>{{items.total_bundles[colour]}}</strong></td>
						{% endif %}
					</tr>
				</table>
			{% endif %}
		{% endfor %}   
	{% endif %}   
	{% if items.accessory_data and items.accessory_data | length > 0 %}
		<h3>Accessories</h3>
		<table style="width:100%;">
			<tr>
				<td class='table-head' style="width:10%;"><strong>S.No</strong></td>
				<td class='table-head' style="width:15%;"><strong>Cloth Type</strong></td>
				<td class='table-head' style="width:20%;"><strong>Colour</strong></td>
				<td class='table-head' style="width:15%;"><strong>Shade</strong></td>
				<td class='table-head' style="width:15%;"><strong>Dia</strong></td>
				<td class='table-head' style="width:25%;"><strong>Weight</strong></td>
			</tr>
			{% for data in items.accessory_data %}
				<tr>
					<td class='table-data'>{{ loop.index }}</td>
					<td class='table-data'>{{ data.cloth_type }}</td>
					<td class='table-data'>{{ data.colour }}</td>
					<td class='table-data'>{{ data.shade }}</td>
					<td class='table-data'>{{ data.dia }}</td>
					<td class='table-data'>{{data.moved_weight}} Kg's</td>
				</tr>
			{% endfor %}	
		</table>
	{% endif %}
</div>

"}]
