css:
td,th{
    width:50px;
}
table{
    width:700px;
}
.headings {
    text-align: center;
    border: 1px solid black; 
    font-weight: bold;
    font-size:12px;
    width:100px;
}
.headings-low {
    text-align: center;
    border: 1px solid black; 
    font-weight: bold;
    font-size:12px;
    width:100px;

}
.datas-no{
    text-align: center;
    border: 1px solid black; 
    font-size:12px;
    width:100px;
}
.datas {
    text-align: center;
    border: 1px solid black; 
    font-size:12px;
    width:100px;
}
.print-format th{
    color:black;
    font-weight:600;
}
.datas-low {
    text-align: center;
    border: 1px solid black; 
    font-size:12px;
    width:100px;
}

.print-format td, .print-format th {
  padding: 6px !important;
}

.print-format td, .print-format th {
  vertical-align: middle !important;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">	</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    {% set width = doc.width_allowance + doc.width %}
    {% set length = doc.length_allowance + doc.length %}
    {% set size_length = { "sizes":0 }%}
    {% set date = get_created_date(doc.creation)%}
    {% set pcs_weight = doc.piece_weight | round(3)%}
    <table style="width:100%">
        <tr>
            <td class="headings" colspan="12">Essdee Knitting Mills Private Limited</td>
        </tr>
        <tr>
            <td class="headings" colspan="12">Cutting Department LaySheet</td>
        </tr>
        <tr>
            <td class="headings" colspan="1">DATE</td>
            <td class="datas"colspan="1">{{date}}</td>
            <td class="headings" colspan="2">LOT NO</td>
            <td class="datas" colspan="1">{{doc.lot}}</td>
            <td class="headings" colspan="2">LAY NO</td>
            <td class="datas" colspan="1">{{doc.lay_no}}</td>
            <td class="headings" colspan="2">REQ LENGTH | WIDTH </td>
            <td class="datas" colspan="2">{{length}} | {{width}}</td>
        </tr>
        <tr>
            <td class="headings" colspan="1" rowspan="2">STYLE</td>
            <td class="datas" colspan="3" rowspan="2">{{doc.item}}</td>
            <td class="headings" colspan="1" rowspan="2">FABRIC</td>
            <td class="datas" colspan="3" rowspan="2"></td>
            <td class="headings" colspan="2" style="font-size:10px;"rowspan="1">ACT LENGTH</td>
            <td class="datas" colspan="2" rowspan="1"></td>
        </tr>
        <tr>
            <td class="headings" colspan="2" style="font-size:10px;" rowspan="1">ACT WIDTH</td>
            <td class="datas" colspan="2" rowspan="1"></td>
        </tr>
        <tr>
            <td class="headings-low" colspan="2">TOTAL BIT</td>
            <td class="datas" colspan="1"></td>
            <td class="headings-low" colspan="2">START TIME</td>
            <td class="datas" colspan="3"></td>
            <td class="headings-low" colspan="2">END TIME</td>
            <td class="datas" colspan="2"></td>
        </tr>
        <tr>
            <td class="headings-low" colspan="1">SIZES</td>
            {% for item in doc.cutting_marker_ratios%}
                {% if size_length.update({"sizes":loop.index })%}{% endif %}
                <td class="datas-no" colspan="1">{{item.size}}</td>
            {% endfor %}
            {% if size_length.sizes < 10 %}
                {% set loop_range = 10 - size_length.sizes %}
                {% for i in range(0,loop_range)%}
                    <td class="datas-no" colspan="1"></td>
                {% endfor %}
            {% endif %}
            <td class="datas-no" colspan="1">TOTAL</td>
        </tr>
        <tr>
            <td class="headings-low" colspan="1">RATIO</td>
            {% for item in doc.cutting_marker_ratios%}
                <td class="datas-no" colspan="1">{{item.ratio}}</td>
            {% endfor %}
            {% if size_length.sizes < 10 %}
                {% set loop_range = 10 - size_length.sizes %}
                {% for i in range(0,loop_range)%}
                    <td class="datas-no" colspan="1"></td>
                {% endfor %}
            {% endif %}
            <td class="datas-no" colspan="1"></td>
        </tr>
        <tr>
            <td class="headings-low" colspan="1">ORD QTY</td>
            {% for item in doc.cutting_marker_ratios%}
                <td class="datas-no" colspan="1"></td>
            {% endfor %}

            {% if size_length.sizes < 10 %}
                {% set loop_range = 10 - size_length.sizes %}
                {% for i in range(0,loop_range)%}
                    <td class="datas-no" colspan="1"></td>
                {% endfor %}
            {% endif %}
            <td class="datas-no" colspan="1"></td>
        </tr>
        <tr>
            <td class="headings-low" colspan="1">CUT QTY</td>
            {% for item in doc.cutting_marker_ratios%}
                <td class="datas-no" colspan="1"></td>
            {% endfor %}
            {% if size_length.sizes < 10 %}
                {% set loop_range = 10 - size_length.sizes %}
                {% for i in range(0,loop_range)%}
                    <td class="datas-no" colspan="1"></td>
                {% endfor %}
            {% endif %}
            <td class="datas-no" colspan="1"></td>
        </tr>
        <tr>
            <td class="headings-low" colspan="1">BALANCE</td>
            {% for item in doc.cutting_marker_ratios%}
                <td class="datas-no" colspan="1"></td>
            {% endfor %}
           {% if size_length.sizes < 10 %}
                {% set loop_range = 10 - size_length.sizes %}
                {% for i in range(0,loop_range)%}
                    <td class="datas-no" colspan="1"></td>
                {% endfor %}
            {% endif %}
            <td class="datas-no" colspan="1"></td>
        </tr>
    </table>
    <table style="width: 100%; border-collapse: collapse; text-align: center;">
        <thead>
            <tr style="width:100%;">
                <th class="headings-low" style="width: 13%;">COLOR</th>
                <th class="headings-low" style="width: 7%;">SHADE</th>
                <th class="headings-low" style="width: 10%;">ROLLS/ ROLL NO</th>
                <th class="headings-low" style="width: 15%;">WEIGHT</th>
                <th class="headings-low" style="width:5%;">BITS</th>
                <th class="headings-low" style="width: 10%;">END_BIT</th>
                <th class="headings-low" style="width: 10%;">BALANCE</th>
                <th class="headings-low" style="width: 10%;">ACCESSORY <span style="font-size: 8px;">(Folding/Piping)</span></th>
                <th class="headings-low" style="width: 20%;">REMARKS</th>
            </tr>
        </thead>
        <tbody>
            {% for item in doc.cutting_laysheet_details %}
            <tr>
                <td class="datas-low" >{{ item.colour }}</td>
                <td class="datas-low" >{{ item.shade }}</td>
                <td class="datas-low" >{{ item.no_of_rolls }}</td>
                <td class="datas-low" >{{ item.weight }}</td>
                <td class="datas-low" >{{ item.no_of_bits }}</td>
                <td class="datas-low" >{{ item.end_bit_weight }}</td>
                <td class="datas-low" >{{ item.balance_weight }}</td>
                <td class="datas-low" >{{ item.accessory_weight }}</td>
                <td class="datas-low">{{ item.comments }}</td>
            </tr>
            {% endfor %}
            {% if doc.cutting_laysheet_details|length == 0 %}
            {% for i in range(0, 12) %}
            <tr>
                <td class="datas-low" style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
                <td class="datas-low"  style="height:30px;"></td>
            </tr>
            {% endfor %}
            {% endif %}
            <tr>
                <td class="headings-low" colspan="1">Total</td>
                <td class="headings-low" colspan="1"></td>
                {% if doc.no_of_rolls > 0 %}
                    <td class="headings-low">{{doc.no_of_rolls}}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                {% if doc.weight > 0%}
                    <td class="headings-low">{{doc.weight}}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                {% if doc.no_of_bits > 0 %}
                    <td class="headings-low">{{doc.no_of_bits}}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                {% if doc.end_bit_weight > 0%}
                    <td class="headings-low">{{doc.end_bit_weight}}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                {% set balance_weight = doc.weight - doc.total_used_weight - doc.accessory_weight%}
                {% if balance_weight %}
                    <td class="headings-low">{{ balance_weight }}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                {% if doc.accessory_weight > 0%}
                    <td class="headings-low">{{doc.accessory_weight}}</td>
                {% else %}
                    <td class="headings-low"></td>
                {% endif %}
                <td class="headings-low" colspan="2"></td>
            </tr>
            <tr>
                <td class="headings" colspan="2">Total Used Weight</td>
                {% if doc.total_used_weight > 0 %}
                    <td class="datas">{{doc.total_used_weight}}</td>
                {% else %}
                    <td class="datas"></td>
                {% endif %}
                <td class="headings" colspan="2">Required Pcs Weight</td>
                {% if doc.required_pcs_weight > 0 %}
                    <td class="datas">{{ doc.required_pcs_weight }}</td>
                {% else %}
                    <td class="datas"></td>
                {% endif %}
                <td class="headings" colspan="2">ACT Pcs Weight</td>
                {% if pcs_weight > 0 %}
                    <td class="datas">{{ pcs_weight }}</td>
                {% else %}
                    <td class="datas"></td>
                {% endif %}    
            </tr>
            <tr>
                <td class="headings" style="height:70px" colspan="3"></td>
                <td class="headings" style="height:70px" colspan="3"></td>
                <td class="headings" style="height:70px" colspan="3"></td>
            </tr>
            <tr>
                <td class="headings" colspan="3">PREPARED BY</td>
                <td class="headings" colspan="3">CHECKED BY</td>
                <td class="headings" colspan="3">APPROVED BY</td>
            </tr>
        </tbody>    
    </table>

</div>"}]
