css:
.table-head, .table-data {
    text-align:center !important;
    border: 1px solid #000;

}
.table-head{
    font-weight:600;
}
.print-format td{
    font-size: 12px !important;
    padding: 4px !important;
}
table{
    width:100%;
}
.avoid-page-break{
    page-break-inside: avoid;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
	{% set ccr_data = get_dict_object(get_ccr(doc.name)) %}
	{% set date = get_created_date(frappe.utils.nowdate()) %}
	{% set time = frappe.utils.nowtime()[:8] %}
	<div style="width:100%;text-align:end;">
		Time: {{ date }} {{ time }}
	</div>
	<div>
		<div><span style="font-weight:600;">Lot:</span> {{doc.lot}}</div>
		<div><span style="font-weight:600;">Item:</span> {{doc.item}}</div>
	</div>
	<h3 style="text-align:center;text-decoration: underline;">CCR</h3>
	{% for marker in ccr_data.marker_data %}
		<div class="avoid-page-break">
			<h4>{{marker}}</h4>
			<table class="avoid-page-break">
				{% set size_total = {} %}
				{% set received_weight = {"rec_sum": 0} %}
				{% set used_weight = {"used_sum": 0} %}
				{% set balance_weight = {"bal_sum": 0} %}
				{% set complete_total = {"total": 0} %}
				<tr>
					<td class="table-head">S.No</td>
					<td class="table-head">Colour</td>
					<td class="table-head">Cloth Type</td>
					<td class="table-head">Received Weight</td>
					<td class="table-head">Used Weight</td>
					<td class="table-head">Balance Weight</td>
					{% for size in ccr_data.sizes %}
						{% set _ = size_total.update({size: 0}) %}
						<td class="table-head">{{size}}</td>
					{% endfor %}
					<td class="table-head">Total</td>
					<td class="table-head">Piece Weight</td>
				</tr>
				{% for (colour, cloth_type) in ccr_data.marker_data[marker] %}
					<tr>
						{% set rec_weight = ccr_data.marker_data[marker][(colour, cloth_type)]['received_weight'] + received_weight['rec_sum'] %}
						{% set use_weight = ccr_data.marker_data[marker][(colour, cloth_type)]['used_weight'] + used_weight['used_sum'] %}
						{% set bal_weight = ccr_data.marker_data[marker][(colour, cloth_type)]['balance_weight'] + balance_weight['bal_sum'] %}
						{% set _ = received_weight.update({"rec_sum": rec_weight}) %}
						{% set _ = used_weight.update({"used_sum": use_weight}) %}
						{% set _ = balance_weight.update({"bal_sum": bal_weight})  %}
						<td class="table-data">{{loop.index}}</td>
						<td class="table-data">{{colour}}</td>
						<td class="table-data">{{cloth_type}}</td>
						<td class="table-data">{{ccr_data.marker_data[marker][(colour, cloth_type)]['received_weight'] | round(3)}}</td>
						<td class="table-data">{{ccr_data.marker_data[marker][(colour, cloth_type)]['used_weight'] | round(3)}}</td>
						<td class="table-data">{{ccr_data.marker_data[marker][(colour, cloth_type)]['balance_weight'] | round(3)}}</td>
						{% for size in ccr_data.sizes %}
							{% set size_val = size_total[size] + ccr_data.marker_data[marker][(colour, cloth_type)][size]['bits'] %}
							{% set _ = size_total.update({size: size_val}) %}
							<td class="table-data">{{ccr_data.marker_data[marker][(colour, cloth_type)][size]['bits'] | int }}</td>
						{% endfor %}
						<td class="table-data">{{ccr_data.marker_data[marker][(colour, cloth_type)]['total_pieces'] | int}}</td>
						{% set piece_weight = ccr_data.marker_data[marker][(colour, cloth_type)]['used_weight']/ccr_data.marker_data[marker][(colour, cloth_type)]['total_pieces'] %}
						<td class="table-data">{{piece_weight | round(4)}}</td>
					</tr>
				{% endfor %}
				<tr>
					<td class="table-head"></td>
					<td class="table-head">Total</td>
					<td class="table-head"></td>
					<td class="table-head">{{received_weight['rec_sum'] | round(3) }}</td>
					<td class="table-head">{{used_weight['used_sum'] | round(3) }}</td>
					<td class="table-head">{{balance_weight['bal_sum'] | round(3) }}</td>
					{% for size in ccr_data.sizes %}
						{% set x = complete_total['total'] + size_total[size] %}
						{% set _ = complete_total.update({"total": x}) %}
						<td class="table-head">{{size_total[size] | int }}</td>
					{% endfor %}	
					<td class="table-head">{{complete_total['total'] | int}}</td>
					<td class="table-head">{{(used_weight['used_sum']/complete_total['total']) | round(4)}}</td>
				</tr>
			</table>
		</div>	
	{% endfor %}
	<table class="avoid-page-break">
		<tr>
			<td colspan="5"><h4>Accessory Summary</h4></td>
		</tr>
		<tr>
			<td class="table-head">S.No</td>
			<td class="table-head">Cloth</td>
			<td class="table-head">Cloth Type</td>
			<td class="table-head">Colour</td>
			<td class="table-head">Dia</td>
			<td class="table-head">Used Weight</td>
		</tr>
		{% set acc_used = {"acc": 0} %}
		{% for cloth in doc.cutting_plan_accessory_details %}
			<tr>
				{% set acc = cloth.used_weight + acc_used['acc'] %}
				{% set _ = acc_used.update({"acc": acc }) %}
				<td class="table-data">{{loop.index}}</td>
				<td class="table-data">{{get_item_from_variant(cloth.cloth_item_variant)}}</td>
				<td class="table-data">{{cloth.cloth_type}}</td>
				<td class="table-data">{{cloth.colour}}</td>
				<td class="table-data">{{cloth.dia}}</td>
				<td class="table-data">{{cloth.used_weight}}</td>
			</tr>
		{% endfor %}
		<tr>
			<td class="table-head"></td>
			<td class="table-head"></td>
			<td class="table-head"></td>
			<td class="table-head"></td>
			<td class="table-head"></td>
			<td class="table-head">{{acc_used['acc'] | round(3)}}</td>
		</tr>
	</table>
	<div class="avoid-page-break">
		<h3 style="text-decoration: underline;text-align:center;">Cloth Received and Used Summary</h3>
		<h4>Cloth Summary</h4>
		{% set cloth_types = {} %}
		{% for cloth in doc.cutting_plan_cloth_details %}
			{% set _ = cloth_types.update({cloth.cloth_type: 0}) %}
		{% endfor %}
		{% for cloth_type in cloth_types %}
			{% set index = {"index": 0} %}
			{% set received_weight = {"rec_sum": 0} %}
			{% set used_weight = {"used_sum": 0} %}
			{% set balance_weight = {"bal_sum": 0} %}

			<table class="avoid-page-break" style="margin-bottom:20px;">
				<tr>
					<td class="table-head">S.No</td>
					<td class="table-head">Cloth</td>
					<td class="table-head">Cloth Type</td>
					<td class="table-head">Colour</td>
					<td class="table-head">Dia</td>
					<td class="table-head">Received Weight</td>
					<td class="table-head">Used Weight</td>
					<td class="table-head">Balance Weight</td>        
				</tr>
				{% for cloth in doc.cutting_plan_cloth_details %}
					{% if cloth.cloth_type == cloth_type %}
						{% set idx = index['index'] + 1 %}
						{% set _ = index.update({"index": idx}) %}
						{% set rec = cloth.weight + received_weight['rec_sum'] %}
						{% set used = cloth.used_weight + used_weight['used_sum'] %}
						{% set balance = cloth.balance_weight + balance_weight['bal_sum'] %}
						{% set _ = received_weight.update({"rec_sum": rec}) %}
						{% set _ = used_weight.update({"used_sum": used}) %}
						{% set _ = balance_weight.update({"bal_sum": balance}) %}
						<tr>
							<td class="table-data">{{index['index']}}</td>
							<td class="table-data">{{get_item_from_variant(cloth.cloth_item_variant)}}</td>
							<td class="table-data">{{cloth.cloth_type}}</td>
							<td class="table-data">{{cloth.colour}}</td>
							<td class="table-data">{{cloth.dia}}</td>
							<td class="table-data">{{cloth.weight | round(3)}}</td>
							<td class="table-data">{{cloth.used_weight | round(3)}}</td>
							<td class="table-data">{{cloth.balance_weight | round(3)}}</td>
						</tr>
					{% endif %}	
				{% endfor %}
				<tr>
					<td class="table-head"></td>
					<td class="table-head"></td>
					<td class="table-head"></td>
					<td class="table-head"></td>
					<td class="table-head"></td>
					<td class="table-head">{{received_weight['rec_sum'] | round(3) }}</td>
					<td class="table-head">{{used_weight['used_sum'] | round(3)}}</td>
					<td class="table-head">{{balance_weight['bal_sum'] | round(3)}}</td>        
				</tr>
			</table>
		{% endfor %}
	</div>	
	<h3 style="text-decoration: underline;text-align:center;">Cutting Completion & Excess Panels Summary</h3>
	{% set items = remove_empty_rows(doc.completed_items_json, "completed") %}
	{% if items %}
		<h4>Completed Pieces</h4>
		<div>
			<table>
				{% for i in items %}
					<tr>
						<td>
							{% if i['items'] %}
								<table class="avoid-page-break">
									{% set primary_sum = {} %}
									<tr>
										<td class="table-head">S.No.</td>
										<td class="table-head">{{ i['pack_attr'] }}</td>
										{% if i['is_set_item'] %}
											<td class="table-head">{{i['set_item_attr']}}</td>
										{% endif %}
										{% for val in i['primary_attribute_values'] %}
											{% set _ = primary_sum.update({val: 0}) %}
											<td class="table-head">{{ val }}</td>
										{% endfor %}
										<td class="table-head">Total</td>
									</tr>
									{% for j in i['items'] %}
										<tr>
											<td class="table-data">{{ loop.index }}</td>
											<td class="table-data">
												{{ j['attributes'][i['pack_attr']] }}
												{% if doc.version == 'V2' or doc.version == 'V3' %}
													{% if j['is_set_item'] and j['attributes'][j['set_attr']] != j['major_attr_value'] and j['attributes'][i['pack_attr']] %}
														({{ j['item_keys']['major_colour'] }})
													{% endif %}
												{% endif %}	
											</td>
											{% if j['is_set_item'] %}
												<td class="table-data">{{ j['attributes'][i['set_item_attr']] }}</td>
											{% endif %}
											{% set row_sum = {"row": 0} %}
											{% for key, val in j['values'].items() %}
												<td class="table-data">
													{% if val > 0 %}
														{% set qty = primary_sum[key] + val %}
														{% set _ = primary_sum.update({key: qty}) %}
														{% set row_qty = row_sum["row"] + val %}
														{% set _ = row_sum.update({"row":row_qty}) %}
														{{ val }}
													{% else %}
														--
													{% endif %}
												</td>
											{% endfor %}
											<td class="table-head"> {{ row_sum["row"] }} </td>
										</tr>
									{% endfor %}
									<tr>
										<td class="table-head">Total</td>
										<td class="table-head"></td>
										{% if i['is_set_item'] %}
											<td class="table-head"></td>
										{% endif %}
										{% set total_sum = {"total": 0 } %}
										{% for val in i['primary_attribute_values'] %}
											{% set tot = total_sum["total"] + primary_sum[val] %}
											{% set _ = total_sum.update({"total": tot}) %}
											<td class="table-head">{{ primary_sum[val] }}</td>
										{% endfor %}
										<td class="table-head">{{ total_sum["total"] }}</td>
									</tr>
								</table>
							{% endif %}
						</td>
					</tr>
				{% endfor %}
			</table>
		</div>
	{% endif %}	
	{% set items = remove_empty_rows(doc.incomplete_items_json, "incompleted") %}
	{% if items %}
		<h4>Excess Panels</h4>
		<div>
			<table>
				{% for i in items %}
					<tr>
						<td>
							{% if i['items'] %}
								<table class="avoid-page-break">
									<tr>
										<td class="table-head">S.No.</td>
										<td class="table-head">{{ i['pack_attr'] }}</td>
										{% if i['is_set_item'] %}
											<td class="table-head">{{i['set_item_attr']}}</td>
										{% endif %}
										{% for val in i['primary_attribute_values'] %}
											<td class="table-head">{{ val }}</td>
										{% endfor %}
									</tr>
									{% for j in i['items'] %}
										<tr>
											<td class="table-data">{{ loop.index }}</td>
											<td class="table-data">
												{{ j['attributes'][i['pack_attr']] }}
												{% if doc.version == 'V2' or doc.version == 'V3' %}
													{% if j['is_set_item'] and j['attributes'][j['set_attr']] != j['major_attr_value'] and j['attributes'][i['pack_attr']] %}
														({{ j['item_keys']['major_colour'] }})
													{% endif %}
												{% endif %}	
											</td>
											{% if j['is_set_item'] %}
												<td class="table-data">{{ j['attributes'][i['set_item_attr']] }}</td>
											{% endif %}
											{% for k, panel_dict in j['values'].items() %}
												<td class="table-data">
													{% for panel, count in panel_dict.items() %}
														{% if count > 0 %}
															{{ panel }} {{ count }}<br>
														{% endif %}
													{% endfor %}
												</td>
											{% endfor %}
										</tr>
									{% endfor %}
								</table>
							{% endif %}
						</td>
					</tr>
				{% endfor %}
			</table>
		</div>
	{% endif %}	
</div>"}]
