css:
.table-bordered {
    border-collapse: collapse;
    width: 100%;
}
.table-bordered td, .table-bordered th {
    border: 1px solid black; 
    padding: 4px;
    text-align: center;
}
.print-format td{
    padding:2px !important;
    font-size: 12px;
}
.print-format th{
    padding:2px !important;
    font-size: 12px;
}
.table-data{
    text-align:center !important;
    border: 1px solid #000 !important;
}
.avoid-page-break{
    page-break-inside: avoid;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div>
    {% set panels, items, is_set_item = get_cutting_plan_size_reports(doc.name) %}
    {% if is_set_item %}
        {% for colour in items %}
            {% set splits = colour.split("-") %}
            {% set length = splits | length %}
            {% set part = splits[length - 1] %}
            <table class="table-bordered" style="margin-top:10px;">
                {% set total_bundles = {} %}
                {% set total = {"total" : 0}%}
                {% set panel_length = panels[part] | length %}
                <tr>
                    <td class="table-data" style="width:20%;" colspan="2"><strong>Lot No:</strong> {{doc.lot}}</td>
                    <td class="table-data" colspan="{{panel_length}}"><strong>Style Name:</strong>  {{doc.item}}</td>
                    <td class="table-data" style="width:20%;"><strong>Colour: </strong> {{colour}}</td>
                </tr>
                <tr>
                    <td class="table-data" style="width:10%;"><strong> S.No</strong> </td>
                    <td class="table-data" style="width:10%;"><strong> Size</strong> </td>
                    {% for panel in panels[part] %}
                        {% set _ = total_bundles.update({panel: 0}) %}
                        <td class="table-data"><strong> {{ panel }}</strong> </td>
                    {% endfor %}
                    <td class="table-data" style="width:20%;"><strong> No of Bundles</strong> </td>
                </tr>
                {% for row in items[colour] %}
                    <tr>
                        <td class="table-data" style="width:10%;">{{ loop.index }}</td>
                        <td class="table-data" style="width:10%;">{{ row.size }}</td>
                        {% set bundle = {"bundle": 0} %}
                        {% for panel in panels[part] %}
                            {% set panel_bundle = panel + "_Bundle" %}
                            {% if panel in row %}
                                {% if row[panel] > 0 %}
                                    {% set bundle_name = panel + "_Bundle" %}
                                    <td class="table-data">{{ row[panel] }}({{row[bundle_name]}})</td>
                                    {% set temp = row[panel_bundle] + total_bundles[panel] %}
                                    {% if total_bundles.update({panel : temp }) %}{% endif %}

                                    {% set total_temp = total['total'] + row[panel_bundle] %}
                                    {% if total.update({"total": total_temp}) %}{% endif %}

                                    {% set b = bundle["bundle"] + row[panel_bundle] %}
                                    {% if bundle.update({"bundle": b}) %}{% endif %}
                                {% else %}
                                    <td class="table-data">0</td>
                                {% endif %}
                            {% else %}
                                <td class="table-data">0</td>
                            {% endif %}    
                        {% endfor %}
                        <td class="table-data" style="width:20%;">{{ bundle["bundle"] }}</td>
                    </tr>
                {% endfor %}
                <tr>
                    <td class="table-data" style="width:10%;">Total</td>
                    <td class="table-data" style="width:10%;"></td>
                    {% for panel in panels[part] %}
                        <td class="table-data">{{ total_bundles[panel] }}</td>
                    {% endfor %}
                    <td class="table-data" style="width:20%;">{{ total['total'] }}</td>
                </tr>
            </table>
        {% endfor %}
    {% else %}
        {% for colour in items %}
            <table class="table-bordered" style="margin-top:10px;">
                {% set total_bundles = {} %}
                {% set total = {"total" : 0}%}
                {% set panel_length = panels | length %}
                <tr>
                    <td class="table-data" style="width:20%;" colspan="2"><strong>Lot No:</strong> {{doc.lot}}</td>
                    <td class="table-data" colspan="{{panel_length}}"><strong>Style Name:</strong>  {{doc.item}}</td>
                    <td class="table-data" style="width:20%;"><strong>Colour: </strong> {{colour}}</td>
                </tr>
                <tr>
                    <td class="table-data" style="width:10%;"><strong> S.No</strong> </td>
                    <td class="table-data" style="width:10%;"><strong> Size</strong> </td>
                    {% for panel in panels %}
                        {% set _ = total_bundles.update({panel: 0}) %}
                        <td class="table-data"><strong> {{ panel }}</strong> </td>
                    {% endfor %}
                    <td class="table-data" style="width:20%;"><strong> No of Bundles</strong> </td>
                </tr>
                {% for row in items[colour] %}
                    <tr>
                        <td class="table-data" style="width:10%;">{{ loop.index }}</td>
                        <td class="table-data" style="width:10%;">{{ row.size }}</td>
                        {% set bundle = {"bundle": 0} %}
                        {% for panel in panels %}
                            {% set panel_bundle = panel + "_Bundle" %}
                            {% if panel in row %}
                                {% if row[panel] > 0 %}
                                    {% set bundle_name = panel + "_Bundle" %}
                                    <td class="table-data">{{ row[panel] }}({{row[bundle_name]}})</td>
                                    {% set b = bundle["bundle"] + row[panel_bundle] %}
                                    {% if bundle.update({"bundle": b}) %}{% endif %}

                                    {% set temp = row[panel_bundle] + total_bundles[panel] %}
                                    {% if total_bundles.update({panel : temp }) %}{% endif %}

                                    {% set total_temp = total['total'] + row[panel_bundle] %}
                                    {% if total.update({"total": total_temp}) %}{% endif %}
                                {% else %}
                                    <td class="table-data">0</td>
                                {% endif %}
                            {% else %}
                                <td class="table-data">0</td>        
                            {% endif %}
                        {% endfor %}
                        <td class="table-data" style="width:20%;">{{ bundle["bundle"] }}</td>
                    </tr>
                {% endfor %}
                <tr>
                    <td class="table-data" style="width:10%;">Total</td>
                    <td class="table-data" style="width:10%;"></td>
                    {% for panel in panels %}
                        <td class="table-data">{{ total_bundles[panel] }}</td>
                    {% endfor %}
                    <td class="table-data" style="width:20%;">{{ total['total'] }}</td>
                </tr>
            </table>
        {% endfor %}    
    {% endif %}  
    <table class="avoid-page-break" style="width:100%;">
        <tr>
            <td colspan="5"><h4>Accessory Summary</h4></td>
        </tr>
        <tr>
            <td class="table-data"><strong>S.No</strong></td>
            <td class="table-data"><strong>Cloth</strong></td>
            <td class="table-data"><strong>Cloth Type</strong></td>
            <td class="table-data"><strong>Colour</strong></td>
            <td class="table-data"><strong>Dia</strong></td>
            <td class="table-data"><strong>Used Weight</strong></td>
        </tr>
        {% set acc_used = {"acc": 0} %}
        {% for cloth in doc.cutting_plan_accessory_details %}
            <tr>
                {% set acc = cloth.used_weight + acc_used['acc'] %}
                {% set _ = acc_used.update({"acc": acc }) %}
                <td class="table-data">{{loop.index}}</td>
                <td class="table-data">{{get_item_from_variant(cloth.cloth_item_variant)}}</td>
                <td class="table-data">{{cloth.cloth_type}}</td>
                <td class="table-data">{{cloth.colour}}</td>
                <td class="table-data">{{cloth.dia}}</td>
                <td class="table-data">{{cloth.used_weight}}</td>
            </tr>
        {% endfor %}
        <tr>
            <td class="table-data"></td>
            <td class="table-data"></td>
            <td class="table-data"></td>
            <td class="table-data"></td>
            <td class="table-data"></td>
            <td class="table-data"><strong>{{acc_used['acc'] | round(3)}}</strong></td>
        </tr>
    </table>
</div>"}]
