css:
th, td{
    border:1px solid gray;
    font-size:13px;
    line-height:1;
}
.print-format p {
    margin: 1px 0px 0px !important;
}
.no-border{
    border:unset;
}
.border-style{
    border: 1px solid gray; 
}

.print-format .letter-head {
  margin-bottom: 10px !important;
}
.border-top-style{
    border: 1px solid gray;
    border-top-left-radius: 10px 10px;
    border-top-right-radius: 10px 10px;
}
.border-bottom-style{
    border: 1px solid gray;
    border-bottom-left-radius: 10px 10px;
    border-bottom-right-radius: 10px 10px 
}
.border-group{
    text-align: center;
    border-bottom: 1px solid gray;
    padding-bottom: 3px;
    width:100%;
}
.font_weight{
    font-weight:600;
}

format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div>	</div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="padding-top:10px;">
    <div class="border-top-style" style="height:50px;text-align:right;">
        <div style="margin-right:10px;">
            <p style="font-size:20px;">Delivery Challan</p>
            <div></div>
            <p style="font-size:12px;">{{doc.name}}</p>
        </div>
    </div>
    <div style="width:100%;height:auto;display:flex;">
        <div class="border-style" style="width:33%;padding:5px;">
            From:<br>
            {{doc.from_address_details}}
        </div>
        <div class="border-style" style="width:34%;padding:5px;">
            To.<br>
            {{doc.supplier_address_details}}
        </div>
        <div class="border-style" style="width:33%;">
            <div class="border-group" style="height:15px;">Date : {{doc.posting_date}}</div>
            <div class="border-group" style="height:15px;">Process: {{doc.process_name}}</div>
            <div class="border-group" style="height:15px;">
                Vehicle No:
                {% if doc.vehicle_no %}
                    <span>{{doc.vehicle_no}}</span>
                {% else %}
                    <span>NA</span>
                {% endif %}
            </div>
            <div style="height:40px;text-align: center;">SCN: 998821</div>
        </div>
    </div>
    <div class="border-style" style="text-align: center;">
        Particulars
    </div>
    <div>
        {% set items = get_dc_structure(doc.name) %}
        <table style="width:100%;">
            {% for i in items %}
                <tr>
                    {% if i.primary_attribute %}
                        <td>
                            {% if i.items() %}
                                <div style="width: 100%;display:flex;height:15px;">
                                    <div style="width: 50%;">Item name: <span class="font_weight">{{i['items'][0]['name']}}</span></div>
                                    <div style="width: 50%;">Lot : <span class="font_weight">{{i.lot}}</span></div>
                                </div>
                                <table style="width:100%;">
                                    {% set primary_values = {} %}
                                    <tr>
                                        <td class="font_weight">S.No.</td>
                                        {% for attr in i.attributes %}
                                            <td class="font_weight">{{attr}}</td>
                                        {% endfor %}
                                        {% for attr in i.primary_attribute_values %}
                                            {% set _ = primary_values.update({attr: 0}) %}
                                            <td class="font_weight">{{attr}}</td>
                                        {% endfor %}
                                        <td class="font_weight">Total</td>
                                    </tr>
                                    {% set rows = {"row_count": 0 } %}
                                    {% for j in i['items'] %}
                                        <tr>
                                            {%if rows.update({"row_count":loop.index})%}{% endif %}
                                            <td>{{loop.index}}</td>
                                            {% for attr in i.attributes %}
                                                <td>
                                                    {% if j.attributes[attr]%}
                                                        {{ j.attributes[attr] }}
                                                    {% endif %}
                                                </td>
                                            {% endfor %}
                                            {% set total = {"total":0} %}
                                            {% for attr in j['values'] %}
                                                {% if j['values'][attr]['delivered_quantity'] > 0 %}
                                                    <td>
                                                        {% set attr_qty = primary_values[attr] + j['values'][attr]['delivered_quantity'] %}
                                                        {% if primary_values.update({attr:attr_qty })%}{% endif %}
                                                        {% set qty = total["total"] + j['values'][attr]['delivered_quantity'] %}
                                                        {% if total.update({"total":qty })%}{% endif %}
                                                        {{j['values'][attr]['delivered_quantity'] | round(3)  }}
                                                        {% if j.default_uom %}
                                                            <span>{{ " " + j.default_uom }}</span>
                                                        {% endif %}
                                                    </td>
                                                {% else %}
                                                    <td>--</td>
                                                {% endif %}
                                            {% endfor %}
                                            <td class="font_weight">{{total["total"] | round(3) }}</td>
                                        </tr>
                                    {% endfor %}
                                    {% if rows['row_count'] > 1 %}
                                        <tr>
                                            <td class="font_weight">Total</td>
                                            {% for attr in i.attributes %}
                                                <td></td>
                                            {% endfor %}
                                            {% for attr in i.primary_attribute_values %}
                                                <td class="font_weight">{{primary_values[attr] | round(3) }}</td>
                                            {% endfor %}
                                            <td></td>
                                        </tr>
                                    {% endif %}
                                </table>
                            {% endif %}
                        </td>
                    {% else %}
                        <td>
                            {% if i.items() %}
                                <div>Lot: <span class="font_weight" style="width:100%;height:15px;">{{i.lot}}</span></div>
                                <table style="width:100%;">
                                    <tr>
                                        <td class="font_weight">S.No.</td>
                                        <td class="font_weight">Item</td>
                                        {% for attr in i.attributes %}
                                            <td class="font_weight">{{attr}}</td>
                                        {% endfor %}
                                        <td class="font_weight">Delivered Quantity</td>
                                    </tr>
                                    {% set total = {"total":0} %}
                                    {% set rows = {"row_count": 0 } %}
                                    {% for j in i['items'] %}
                                        <tr>
                                            {%if rows.update({"row_count":loop.index})%}{% endif %}
                                            <td>{{ loop.index }}</td>
                                            <td>{{ j.name }}</td>
                                            {% for attr in i.attributes %}
                                                <td>{{ j.attributes[attr] }}</td>
                                            {% endfor %}
                                            <td>
                                                {% if j['values']["default"]['delivered_quantity'] > 0 %}
                                                    {% set _ = total["total"] + j['values']["default"]['delivered_quantity'] %}
                                                    {% if total.update({ "total":_ }) %}{% endif %}
                                                    {{ j['values']["default"]['delivered_quantity'] | round(3) }}
                                                    {% if j.default_uom %}
                                                        <span>{{ " " + j.default_uom }}</span>
                                                    {% endif %}
                                                {% else %}
                                                    --
                                                {% endif %}
                                            </td>
                                        </tr>
                                    {% endfor %}
                                    {%if rows['row_count'] > 1%}
                                        <tr>
                                            <td></td>
                                            <td class="font_weight">Total</td>
                                            {% for attr in i.attributes %}
                                                <td></td>
                                            {% endfor %}
                                            <td class="font_weight">{{ total["total"] | round(3) }}</td>
                                        </tr>
                                    {% endif %}
                                </table>
                            {% endif %}
                        </td>
                    {% endif %}
                </tr>
            {% endfor %}
        </table>
    </div>
    <div class="border-style" style="height:20px;width:100%">
        Estimated value of Goods: ₹ {{doc.total_value}}
    </div>
    <div style="height:90px;width:100%;display:flex;">
        <div class="border-style font_weight" style="height:90px;width:50%">
            <div style="text-align:center;height: 15px;">
                Receiver's Signature
            </div>
            <div style="height:50px;">
            </div>
            <div class="font_weight" style="display:flex;height:15px;width:100%;">
                <div style="height: 15px;width:50%;text-align: left;padding-left:5px;">
                    Name:
                </div>
                <div style="height: 15px;width:50%;text-align: left;">
                    Designation:
                </div>
            </div>
        </div>
        <div class="border-style font_weight" style="height:90px;width:50%">
            <div style="text-align:right;height: 15px;padding-right: 15px;">
                For Essdee Knitting Mills Private Limited
            </div>
            <div style="height:50px;">
            </div>
            <div style="height:15px;width:100%;text-align:right;padding-right: 15px;">
                Authorised Signatory
            </div>
        </div>
    </div>
    {% set user_current_time = get_current_user_time() %}
    {% set user = user_current_time[0] %}
    {% set current_time = user_current_time[1] %}
    <div class="border-bottom-style font_weight" style="display: flex;height: 20px;width: 100%;">
        <div style="width: 50%;padding-left: 15px;">
            Print Time: {{current_time}}
        </div>
        <div style="width: 50%;padding-left: 15px;">
            Print User : {{user}}
        </div>
    </div>
</div>"}]
