css:
.print-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
    line-height: 1.6;
}

.print-table th,
.print-table td {
    border: 1px solid #444;
    padding: 4px 6px;
    vertical-align: middle;
}

.print-table td:first-child {
    font-weight: bold;
    background: #fafafa;
}

.empty-row td { 
    color: transparent;
}

.print-table tr {
    page-break-inside: avoid;
}

.table-print-portrait,
.table-print-landscape {
    display: none;
}

@media print and (orientation: portrait) {
    .table-print-portrait { display: block; }
    .table-print-landscape { display: none; }
}

@media print and (orientation: landscape) {
    .table-print-landscape { display: block; }
    .table-print-portrait { display: none; }
}

@media screen {
    .table-print-portrait { display: block; }
}
.print-format td{
    padding: 1px !important;
    font-size: 15px;
    text-align: center;
}

